import axios from 'axios';
import { useEffect } from 'react';
import { useLocation } from 'react-router-dom'
import './singlepost.css'

export default function Singlepost() {
    const loc = useLocation();
    const path = loc.pathname.split("/")[2];
    const [post, setPost] = useState({});

    useEffect(() => {
        const getPost = async () =>{
            const res = await axios.get(`http://localhost:5000/blog/posts/:${path}`);
            setPost(res.data);
        }
    }, [path]);
    return (
        <div className="singlepost">
            <div className="singlePostWrapper">
                <img   
                    src="https://images.unsplash.com/photo-1504805572947-34fad45aed93?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=750&q=80" 
                    alt="" 
                    className="singlePostImg"
                />
                <h1 className="singlePostTitle">
                    Lorem ipsum dolor sit amet consectetur adipisicing elit.
                    <div className="singlePostEdit">
                        <i className="singlePostIcon fas fa-edit"></i>
                        <i className="singlePostIcon fas fa-trash"></i>
                    </div>
                </h1>
                <div className="singlePostInfo">
                    <span className="singlePostAuthor">
                        Author: <b>Memo</b>
                    </span>
                    <span className="singlePostDate">1 hour ago</span>
                </div>
                <p className="singlePostDesc"> 
                    Lorem ipsum, dolor sit amet consectetur adipisicing elit.
                    Labore earum exercitationem architecto quam sequi possimus eius nemo assumenda rerum, 
                    rem eos quae perspiciatis ab quasi, modi a ducimus aliquam reiciendis.
                    Lorem ipsum, dolor sit amet consectetur adipisicing elit.
                    Labore earum exercitationem architecto quam sequi possimus eius nemo assumenda rerum, 
                    rem eos quae perspiciatis ab quasi, modi a ducimus aliquam reiciendis.
                    Lorem ipsum, dolor sit amet consectetur adipisicing elit.
                    Labore earum exercitationem architecto quam sequi possimus eius nemo assumenda rerum, 
                    rem eos quae perspiciatis ab quasi, modi a ducimus aliquam reiciendis.
                </p>
            </div>
        </div>
    )
}
