import axios from 'axios';
import { useContext, useState } from 'react'
import Sidebar from '../../components/sideBar/Sidebar'
import { context } from '../../context/Context'
import './settings.css'

export default function Settings() {
    const {user} = useContext(context);
    const [file, setFile] = useState('');
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [success, setSuccess] = useState(false);

    const handleSubmit = async (e) =>{
        e.preventDefault();
        const updatedUser = {
            userId: user._id,
            username,
            email,
            password,
        };
        if(file){
            const data = new FormData();
            const filename = Date.now() + file.name;
            data.append("name",filename);
            data.append("file",file);
            updatedUser.pp = filename;

            try {
                await axios.post('http://localhost:5000/uploadimg', data);
                setSuccess(true)
            } catch (err){}    
        }
            try {
                await axios.put("http://localhost:5000/blog/users/"+user._id, updatedUser);
            } catch (err) {}

        
    }; 
    return (
        <div className="settings">
            <div className="settingsWrapper">
                <div className="settingsTitle">
                    <span className="settingsUpdateTitle">Update Your Account</span>
                    <span className="settingsDeleteTitle">Delete Account</span>
                </div>
                <form className="settingsForm" onSubmit={handleSubmit}>
                    <label>Profile Picture</label>
                    <div className="settingsPP">
                        <img 
                            src={user.pp}
                            alt=""
                        />
                        <label htmlFor="fileInput">
                            <i className="settingsPPIcon fas fa-user"></i>
                        </label>
                        <input type="file" id="fileInput" style={{ display:'none' }} onChange={e=>setFile(e.target.files[0])} />
                    </div>
                    <label>Username</label>
                    <input type="text" placeholder={user.username} onChange={(e)=>setUsername(e.target.value)}/>
                    <label>Email</label>
                    <input type="email" placeholder={user.email} onChange={(e)=>setEmail(e.target.value)}/>
                    <label>Password</label>
                    <input type="password" onChange={(e)=>setPassword(e.target.value)}/>
                    <button className="settingsSubmit" type="submit">Update</button>
                    {success && <span style={{ color:green }}>Profile is updated...</span>}
                </form>
            </div>
            <Sidebar/>
        </div>
    )
}
