import axios from 'axios';
import React from 'react'
import "./forgotPassword.css"

export default function ForgotPassword() {
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post('http://localhost:5000/blog/forgot-password', { email });
            res.data && window.location.replace('http://localhost:3000/login');
        } catch (err) {
            setError(true);
        }
    }
    return (
        <div className="forgotPassword">
            <span className="fpTitle">Reset Password</span>
            <form className="fpForm" onSubmit={handleSubmit}>
                <label>E-mail</label>
                <input type="text" className="fpInput" placeholder="Enter Your Email..." />
                <button className="fpButton" type="submit">Submit</button>
            </form>
        </div>
    )
}
