import { useContext, useState } from 'react'
import Sidebar from '../../components/sideBar/Sidebar'
import { context } from '../../context/Context'
import './settings.css'

export default function Settings() {
    const {user} = useContext(context);
    const [file, setFile] = useState('');
    const handleSubmit = async (e) =>{
        e.preventDefault();
        const newPost = {
            username: user.username,
            title,
            desc,
        };
        if(file){
            const data = new FormData();
            const filename = Date.now() + file.name;
            data.append("name",filename);
            data.append("file",file);
            newPost.photo = filename;

            try {
                await axios.post('http://localhost:5000/uploadimg', data);
            } catch (err){}    
        }
            try {
                const res = await axios.post("http://localhost:5000/blog/posts/new", newPost);
                window.location.replace('http://localhost:3000/post/'+res.data._id);
            } catch (err) {}

        
    }; 
    return (
        <div className="settings">
            <div className="settingsWrapper">
                <div className="settingsTitle">
                    <span className="settingsUpdateTitle">Update Your Account</span>
                    <span className="settingsDeleteTitle">Delete Account</span>
                </div>
                <form className="settingsForm">
                    <label>Profile Picture</label>
                    <div className="settingsPP">
                        <img 
                            src={user.pp}
                            alt=""
                        />
                        <label htmlFor="fileInput">
                            <i className="settingsPPIcon fas fa-user"></i>
                        </label>
                        <input type="file" id="fileInput" style={{ display:'none' }} />
                    </div>
                    <label>Username</label>
                    <input type="text" placeholder="name"/>
                    <label>Email</label>
                    <input type="email" placeholder="123@gmail.com"/>
                    <label>Password</label>
                    <input type="password" />
                    <button className="settingsSubmit">Update</button>
                </form>
            </div>
            <Sidebar/>
        </div>
    )
}
