import { useContext, useState } from 'react'
import './write.css'
import axios from 'axios'
import { context } from "../../context/Context";


export default function Write() {
    const [title, setTitle] = useState('');
    const [desc, setDesc] = useState('');
    const [file, setFile] = useState('');
    const {user} = useContext(context);
    const handleSubmit = async (e) =>{
        e.preventDefault();
        const newPost = {
            username: user.username,
            title,
            desc,
        };
        if(file){
            const data = FormData();
            const filename = Date.now() + file.name;
            data.append("name",filename);
            data.append("file",file);
            newPost.photo = filename;

            try {
                await axios.post('http://localhost:5000/blog/posts/new');
            } catch (err){}    
        }
            await axios.post("/posts", newPost);
        
    } 
    return (
        <div className="write">
            <img
                className="writeImg" 
                src="https://images.unsplash.com/photo-1629233795404-d8de1dc90aa3?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1500&q=80" 
                alt=""
            />
            <form action="" className="writeForm" onSubmit={handleSubmit}>
                <div className="writeFormGroup">
                    <label htmlFor="fileInput">
                        <i className="writeIcon fas fa-plus"></i>
                    </label>
                    <input type="file" id="fileInput" style={{display:'none'}}/>
                    <input type="text" placeholder="Title" className="writeInput" autoFocus={true} />
                </div>
                <div className="writeFormGroup">
                    <textarea placeholder="Tell your story..." type="text" className="writeInput writeText">
                    </textarea>
                </div>
                <button className="writeSubmit" type="submit">publish</button>
            </form>
        </div>
    )
}
